mapper.select:
SELECT * FROM shop_statistics
WHERE shop_id = #{shopId}
  AND stat_date = #{statDate}
LIMIT 1

SELECT * FROM shop_statistics
WHERE shop_id = #{shopId}
  AND stat_date BETWEEN #{startDate} AND #{endDate}
ORDER BY stat_date DESC

SELECT * FROM shop_statistics
WHERE merchant_id = #{merchantId}
  AND stat_date BETWEEN #{startDate} AND #{endDate}
ORDER BY stat_date DESC
