mapper.select:
SELECT * FROM search_history
WHERE wx_id = #{wxId}
  AND is_delete = 0
ORDER BY created_time DESC
LIMIT #{limit}

mapper.delete:
UPDATE search_history
SET is_delete = 1
WHERE wx_id = #{wxId}
  AND keyword = #{keyword}
  AND is_delete = 0

mapper.select:
SELECT COUNT(*)
FROM search_history
WHERE wx_id = #{wxId}
  AND created_time BETWEEN #{startTime} AND #{endTime}
  AND is_delete = 0
